import React, { useState } from "react";

const TodoInput = () => {
  return (
    <div>
      <InputBox />
      <SubmitButton />
    </div>
  );
};

const InputBox = () => {
  return (
    <input type="text" placeholdeår="할 일을 입력해주세요." id="input-box" />
  );
};

const SubmitButton = () => {
  return (
    <button type="button" id="submit-button">
      등록
    </button>
  );
};

const TodoList = () => {
  const [todos, setTodos] = useState([]);

  const addTodo = (todo) => {
    setTodos([...todos, todo]);
  };

  return (
    <div>
      <InputBox addTodo={addTodo} />
      <SubmitButton />
      <ul>
        {todos.map((todo, index) => (
          <li key={index}>{todo}</li>
        ))}
      </ul>
    </div>
  );
};
export default TodoInput;
